import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Movie, Genre } from '../models/movie.model';

@Injectable({
    providedIn: 'root'
})
export class ApiService {
    private baseUrl = 'https://localhost:7048/api';

    private httpOptions = {
        headers: new HttpHeaders({
            'Content-Type': 'application/json'
        })
    };

    constructor(private http: HttpClient) { }

    // Movie Methods
    getMovies(): Observable<Movie[]> {
        return this.http.get<Movie[]>(`${this.baseUrl}/Movie`);
    }

    getMovie(id: number): Observable<Movie> {
        return this.http.get<Movie>(`${this.baseUrl}/Movie/${id}`);
    }

    createMovie(movie: Movie): Observable<Movie> {
        return this.http.post<Movie>(`${this.baseUrl}/Movie`, movie, this.httpOptions);
    }

    updateMovie(id: number, movie: Movie): Observable<Movie> {
        return this.http.put<Movie>(`${this.baseUrl}/Movie/${id}`, movie, this.httpOptions);
    }

    deleteMovie(id: number): Observable<any> {
        return this.http.delete(`${this.baseUrl}/Movie/${id}`);
    }

    // Genre Methods
    getGenres(): Observable<Genre[]> {
        return this.http.get<Genre[]>(`${this.baseUrl}/Genre`);
    }

    getGenre(id: number): Observable<Genre> {
        return this.http.get<Genre>(`${this.baseUrl}/Genre/${id}`);
    }

    createGenre(genre: Genre): Observable<Genre> {
        return this.http.post<Genre>(`${this.baseUrl}/Genre`, genre, this.httpOptions);
    }

    updateGenre(id: number, genre: Genre): Observable<Genre> {
        return this.http.put<Genre>(`${this.baseUrl}/Genre/${id}`, genre, this.httpOptions);
    }

    deleteGenre(id: number): Observable<any> {
        return this.http.delete(`${this.baseUrl}/Genre/${id}`);
    }
}
